function extractNoticeMetadata() {
  const getText = (selector, parent = document) => {
    const el = parent.querySelector(selector);
    return el ? el.textContent.trim() : null;
  };

  const getMultipleDataFields = (prefix, parent) => {
    const fields = {};
    parent.querySelectorAll('div').forEach(div => {
      const label = div.querySelector('.label');
      const data = div.querySelector('.data, .line');
      if (label && data) {
        const key = label.textContent.trim().replace(/:$/, '');
        const value = data.textContent.trim();
        fields[`${prefix}: ${key}`] = value;
      }
    });
    return fields;
  };

  const metadata = {};

  // ID du document et Titre
  const titleEl = document.querySelector('#notInPdf');
  if (titleEl) {
    const parts = titleEl.textContent.trim().split(' - ');
    if (parts.length > 1) {
      metadata['document_id'] = parts[0];
      metadata['title'] = parts.slice(1).join(' - ');
    }
  }

  // Buyer + email
  const buyerSection = document.querySelector('#section1_1')?.nextElementSibling;
  if (buyerSection) {
    metadata['buyer'] = getText('div span.data', buyerSection);
    metadata['email'] = getText('a[href^="mailto:"]', buyerSection);
  }

  // Section "2. Procedure"
  const procedureSection = document.querySelector('#section2_3')?.nextElementSibling;
  if (procedureSection) {
    Object.assign(metadata, getMultipleDataFields('procedure', procedureSection));
  }

  // Section "notice information"
  const noticeSection = document.querySelector('#section_23')?.nextElementSibling;
  if (noticeSection) {
    Object.assign(metadata, getMultipleDataFields('notice_informations', noticeSection));
  }

  return { metadata };
}
return extractNoticeMetadata();
